@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root{
  --bg:#0a0d1a; --fg:#e1e7ff; --muted:#7a8cdb; --card:rgba(30,60,255,.08);
  --blue1:#1e3aff; --blue2:#4d7fff; --blue3:#80b3ff; --ring:rgba(30,58,255,.55);
  --radius:16px; --max:1200px;
  --navy:#0a0d1a; --dark:#060918; --electric:#1e3aff;

  /* Elite Executive Theme Colors */
  --executive-accent: #4d7fff;
  --executive-gold: #FFD700;
  --executive-platinum: #E5E4E2;
  --executive-diamond: #B9F2FF;
  --text-gradient: linear-gradient(135deg, #e1e7ff 0%, #7a8cdb 100%);
  --glow-blue: rgba(30, 58, 255, 0.4);
  --glow-gold: rgba(255, 215, 0, 0.4);
  --glow-platinum: rgba(229, 228, 226, 0.3);
  --shadow-deep: rgba(6, 9, 24, 0.8);
  --shadow-elite: rgba(0, 0, 0, 0.9);

  /* Typography */
  --font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', Consolas, monospace;

  /* Mobile App Theme */
  --mobile-nav-height: 64px;
  --mobile-safe-area: env(safe-area-inset-top);
  --app-spacing: 72px;
  --card-spacing: 16px;

  /* Consistent Spacing Scale */
  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
  --spacing-2xl: 3rem;
  --spacing-3xl: 4rem;

  /* Borders & Radius */
  --radius-sm: 0.25rem;
  --radius-md: 0.5rem;
  --radius-lg: 1rem;
  --radius-xl: 1.5rem;
  --radius-full: 9999px;

  /* Transitions */
  --transition-fast: 0.15s ease;
  --transition-normal: 0.3s ease;
  --transition-slow: 0.5s ease;
  --transition-luxury: 0.8s cubic-bezier(0.23, 1, 0.320, 1);

  /* Z-index */
  --z-index-dropdown: 1000;
  --z-index-sticky: 1020;
  --z-index-fixed: 1030;
  --z-index-modal-backdrop: 1040;
  --z-index-modal: 1050;
  --z-index-popover: 1060;
  --z-index-tooltip: 1070;
}

.dark {
  --background: hsl(220, 8%, 4%);
  --foreground: hsl(0, 0%, 88%);
  --card: hsl(216, 18%, 7%);
  --card-foreground: hsl(0, 0%, 88%);
  --popover: hsl(216, 18%, 7%);
  --popover-foreground: hsl(0, 0%, 88%);
  --primary: hsl(51, 100%, 58%);
  --primary-foreground: hsl(220, 8%, 4%);
  --secondary: hsl(215, 15%, 17%);
  --secondary-foreground: hsl(0, 0%, 88%);
  --muted: hsl(215, 15%, 17%);
  --muted-foreground: hsl(0, 0%, 50%);
  --accent: hsl(215, 15%, 17%);
  --accent-foreground: hsl(51, 100%, 58%);
  --destructive: hsl(0, 91%, 63%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(215, 15%, 17%);
  --input: hsl(215, 15%, 17%);
  --ring: hsl(51, 100%, 58%);
}

@layer base {
  * {
    @apply border-border;
  }

  body{
    background: var(--bg);
    background-image:
      radial-gradient(circle at 20% 50%, rgba(30,60,255,0.15) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(255,215,0,0.08) 0%, transparent 50%),
      radial-gradient(circle at 40% 80%, rgba(185,242,255,0.06) 0%, transparent 50%),
      linear-gradient(135deg, rgba(10,13,26,0.98) 0%, rgba(6,9,24,0.95) 100%);
    color: var(--fg);
    font-family: var(--font-family);
    min-height: 100vh;
    @apply font-sans antialiased;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  .container{max-width:var(--max);margin:0 auto;padding:0 1.5rem;width:100%;box-sizing:border-box;}

  /* Enhanced Glass Morphism Effects */
  .glass{
    background: rgba(30,60,255,0.12);
    backdrop-filter: blur(24px) saturate(180%);
    -webkit-backdrop-filter: blur(24px) saturate(180%);
    border: 1px solid rgba(255,215,0,0.2);
    border-radius: var(--radius);
    box-shadow: 
      0 8px 32px rgba(6,9,24,0.4), 
      0 0 24px rgba(30,60,255,0.1),
      inset 0 1px 0 rgba(255,215,0,0.2),
      inset 0 -1px 0 rgba(255,255,255,0.05);
    position: relative;
    overflow: hidden;
    transition: all var(--transition-luxury);
  }

  .glass:hover{
    border-color: rgba(255,215,0,0.4);
    box-shadow: 
      0 16px 48px rgba(6,9,24,0.6), 
      0 0 40px rgba(255,215,0,0.2),
      0 0 60px rgba(30,60,255,0.15),
      inset 0 2px 0 rgba(255,215,0,0.3),
      inset 0 -2px 0 rgba(255,255,255,0.1);
    transform: translateY(-4px) scale(1.02);
    background: rgba(30,60,255,0.18);
  }

  /* Elite Button Styles */
  .btn{
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.8rem 1.5rem;
    border-radius: 12px;
    border: 1px solid rgba(255,215,0,0.3);
    text-decoration: none;
    position: relative;
    overflow: hidden;
    background: linear-gradient(135deg, rgba(255,215,0,0.1), rgba(30,60,255,0.1));
