    transition: all var(--transition-luxury);
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    font-size: 0.875rem;
  }

  .btn.primary{
    background: linear-gradient(135deg, var(--executive-gold), #FFED4A);
    color: var(--navy);
    border-color: var(--executive-gold);
    font-weight: 700;
    box-shadow: 
      0 8px 24px rgba(255,215,0,0.3),
      inset 0 1px 0 rgba(255,255,255,0.3);
  }

  /* Elite KPI Ribbons */
  .kpi-ribbon{
    display: inline-flex;
    gap: 0.5rem;
    align-items: center;
    border-radius: 999px;
